/* eslint-disable @typescript-eslint/no-unused-expressions */
import styled from 'styled-components';
import background from "../public/Imagens/infinito.png"


export const Image = styled.div`
backgroundImage: url(/Imagens/infinito.png) 
backgroundImage: url(${process.env.PUBLIC_URL + '/todo/public/Imagens/infinito.png'})
height: 156px;
width: 93.95%; 

`;



export const Container = styled.div`
    background-color: #16366c;
    color: #797A81;
    min-height: 100vh;
`;

export const Area = styled.div`
    margin: auto;
    max-width:980px;
    padding:10px
`;

export const Header = styled.h1`
    margin:0;
    padding:0;
    color:#FFF;
    text-align: center;
    border-bottom: 1px solid #444;
    padding-bottom:20px;
`;
